const PopulateTodayRaces=()=>{$.getJSON({url:"https://us-west-2.aws.data.mongodb-api.com/app/races-bwsnh/endpoint/current_races/v2",crossDomain:true,success:s=>$("#todaystracks .items").html(t(s)),error:s=>{console.log("fail");console.log(s)}})};const t=s=>s.todaysraces.tracks.filter((s=>!s.resultsUrl.includes("%7Bname%7D"))).map((s=>`<a href="${s.resultsUrl}" alt="${s.name} Results" title="current race: ${s.currentRace} mtp: ${s.mtp}" class="item btn btn-default">${s.name}</a>`)).join("");