function PopulateTodaysRaces(){const TodaysRaces=todaysraces=>todaysraces.tracks.filter((track=>!track.resultsUrl.includes("%7Bname%7D"))).map((track=>`<a href="${track.resultsUrl}" alt="${track.name} Results" title="current race: ${track.currentRace} mtp: ${track.mtp}" class="item btn btn-default">${track.name}</a>`)).join("\n");if(typeof todaysRaces==="undefined"||!("tracks"in todaysRaces)){$.getJSON({url:"https://us-west-2.aws.data.mongodb-api.com/app/races-bwsnh/endpoint/current_races/v2",crossDomain:true}).done((data=>{todaysRaces=data.todaysraces;$("#todaystracks .items").html(TodaysRaces(todaysRaces))})).fail(((jqXHR,status,err)=>{console.log("failed to load current races [PopulateTodaysRaces()]");console.log(err)}))}else{$("#todaystracks .items").html(TodaysRaces(todaysRaces))}}
