function PopulateTodaysRaces(){const TodaysRaces=todaysraces=>todaysraces.tracks.filter((track=>!track.resultsUrl.includes("{name}"))).map((track=>`<a href="${track.resultsUrl}" alt="${track.name} Results" title="current race: ${track.currentRace} mtp: ${track.mtp}" class="item btn btn-default">${track.name}</a>`)).join("\n");if(typeof todaysRaces==="undefined"||!("tracks"in todaysRaces)){$.getJSON({url:"https://ojlbo7v5hb.execute-api.us-west-2.amazonaws.com/current_races/v2",crossDomain:true}).done((data=>{todaysRaces=data.todaysraces;$("#todaystracks .items").html(TodaysRaces(todaysRaces))})).fail(((jqXHR,status,err)=>{console.log("failed to load current races [PopulateTodaysRaces()]");console.log(err)}))}else{$("#todaystracks .items").html(TodaysRaces(todaysRaces))}}
